    def test_finalize(self):
        psbt = PSBT.parse_base64('')
        psbt.finalize()
        self.assertTrue(psbt.validate())
        want = ''
        self.assertEqual(psbt.serialize_base64(), want)
        tx_obj = psbt.final_tx()
        want = ''
        self.assertEqual(tx_obj.serialize().hex(), want)
